mapper.select:
	select num, title, userid, content, to_char(writeday,'YYYY/MM/dd') writeday, readCnt
    from Board
    where num = #{num}

mapper.update:
update Board
set readCnt = readCnt + 1
where num = #{num}

update Board
set title =#{title}, userid=#{userid}, content=#{content}, writeday=SYSDATE
where num=#{num}
